// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./interfaces/IRegistry.sol";
import {MicroGrantsStrategy} from "./MicroGrantsStrategy.sol";

interface IAlloV2 {
    function createPoolWithCustomStrategy(
        bytes32 _profileId,
        address _strategy,
        bytes memory _initStrategyData,
        address _token,
        uint256 _amount,
        Metadata memory _metadata,
        address[] memory _managers
    ) external payable returns (uint256 poolId);

    function getRegistry() external view returns (IRegistry);
}


contract TestContract {
    IRegistry registry;
    IAlloV2 allo;

    struct InitializeParams {
        bool useRegistryAnchor;
        uint64 allocationStartTime;
        uint64 allocationEndTime;
        uint256 approvalThreshold;
        uint256 maxRequestedAmount;
    }

    constructor() {
        address registryAddress = 0x4AAcca72145e1dF2aeC137E1f3C5E3D75DB8b5f3;
        registry = IRegistry(registryAddress);

        address alloAddress = 0x1133eA7Af70876e64665ecD07C0A0476d09465a1;
        allo = IAlloV2(alloAddress);
    }

    function getProfile(bytes32 profileId) public view returns (IRegistry.Profile memory) {
        return registry.getProfileById(profileId);
    }

    function createProfile( 
        uint256 _nonce,
        string memory _name,
        Metadata memory _metadata,
        address _owner,
        address[] memory _members
    ) external returns (bytes32 profileId) {

        return registry.createProfile( _nonce, _name, _metadata, _owner, _members);
    }

    function getAlloRegistry() external view returns (IRegistry) {
        return allo.getRegistry();
    }

    function createPoolInAllo(
        bytes32 _profileId,
        address _strategy,
        address _token,
        uint256 _amount,
        Metadata memory _metadata,
        address[] memory _managers
    ) public payable returns (uint256) {

        // Create an instance of InitializeParams with the correct structure
         InitializeParams memory initData = InitializeParams({
            useRegistryAnchor: true, // or false, depending on your needs
            allocationStartTime: uint64(block.timestamp), // uint64 value for start time
            allocationEndTime: uint64(block.timestamp + 1 days), // uint64 value for end time
            approvalThreshold: 1e18, // uint256 value for threshold
            maxRequestedAmount: 3 // uint256 value for max amount
        });

        // Encode the initData using Solidity's abi.encode function
        bytes memory encodedInitData = abi.encode(initData);

        // Rest of your function logic...
        return allo.createPoolWithCustomStrategy{value: msg.value}(
            _profileId,
            _strategy,
            encodedInitData,
            _token,
            _amount,
            _metadata,
            _managers
        );
    }

}

